import React,{useState,useCallback,useEffect} from 'react';
import {Form, Input, Checkbox, Button} from 'antd';
import Router from 'next/router';

import {useDispatch, useSelector} from 'react-redux';
import {signUpRequestAction, LOAD_FOLLOW_REQUEST} from '../reducers/user';


export const useInput = (initValue = null) => {
    const [value,setter] = useState(initValue);
    const handler = useCallback( (e) =>{
        setter(e.target.value);
    },[]);
    return [value, handler]; 
};

const Signup = () => {
    const [userCredentials, setCredentials]= useState({
        id: '',
        nickname: '', 
        email: '',
        password: '',
        password_chk :'',
        term: false, 
    })
    const {id,nickname,password} = userCredentials;
    const [passwordError, setPasswordError] = useState(false);
    const [termError, setTermError] = useState(false);
    const dispatch = useDispatch();
    const {isSigningUp, me} = useSelector((state) => state.user);
    useEffect( () => {
        alert('로그인 했으니 메인페이지로 이동합니다. ')
        Router.push('/')
    },[me && me.id]);

    const onSubmit = (e) => {
        e.preventDefault();
        const {password_chk, term} = userCredentials;
        if(password_chk !== pass){
            setPasswordError(true);
            return false;  
        } 
        if(!term){
            setTermError(true); 
            return false; 
        }
        setTermError(false); 
        setPasswordError(false);
        dispatch(signUpRequestAction({
            userId: id,
            nickname,
            password
        }))
    }
    const onChange = (e) => {
        const {name,value} = e.target;
        if(name==='term'){
            setCredentials({...userCredentials, [name]: e.target.checked});
        }
        else setCredentials({...userCredentials, [name]:value});
    }

    return (
        <>
            <Form onSubmit={onSubmit} style={{padding:10}}>
                <div>
                    <label htmlFor="id">아이디</label>
                    <br/>
                    <Input name="id" required onChange={onChange}/>
                </div>
                <div>
                    <label htmlFor="nickname">닉네임</label>
                    <br/>
                    <Input name="nickname" required onChange={onChange}/>
                </div>
                <div>
                    <label htmlFor="password">비밀번호</label>
                    <br/>
                    <Input name="password" type="password" required onChange={onChange}/>
                </div>
                <div>
                    <label htmlFor="pass_chk">비밀번호 확인</label>
                    {
                        passwordError ? 
                        <p>비밀번호가 일치하지 않습니다.</p>:
                        null
                    }
                    <br/>
                    <Input name="pass_chk" type="password" required onChange={onChange}/>
                </div>
                <div>
                    <Checkbox name="term"  required checked={userCredentials.term} onChange={onChange}>회원가입에 동의합니다.</Checkbox>
                    {
                        termError ?
                        <span style={{color:'blue'}}>약관에 동의해주세요.</span>: 
                        null 
                    }
                </div>
                <div>
                    <Button type="primary" htmlType="submit" loading={isSigningUp}>가입하기</Button>
                </div>
            </Form>
        </>
    )
};

export default Signup;
