import React,{useCallback} from 'react';
import {Form, Input, Button} from 'antd';
import {useSelector, useDispatch} from 'react-redux';
import { setContext } from 'redux-saga/effects';
const PostForm = () => {
    const dispatch = useDispatch();
    const [text,setText] = useState('');
    const {imagePaths, isAddingPost} = useSelector( (state) => state.post); 
    const onSubmitForm = usecallback(() => {

    },[]);
    const onChangeText = useCallback((e) => {
        setContext(e.target.value);
    });
    
    return (
    <Form style={{margin: '10px 0 20px'}} encType="multipart/form-data" onSubmit={onSubmitForm}>
                    <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 있었나요?" onChange = {onChangeText}/>    
                    <div>
                        <input type="file" multiple hidden />
                        <Button> 이미지 업로드</Button>
                        <Button type="primary" style={{float:'right'}} htmlType="submit" isLoading={isAddingPost}>짹쨱</Button>
                    </div>
                    <div>
                        {imagePaths.map( (v,i) => {
                            return (
                                <div key={v} style={{display: 'inline-block'}}>
                                    <img src={'http://localhost:3000/' + v} style={{width:'200px'}} alt={v} />
                                    <div>
                                        <Button>제거</Button>
                                    </div>
                                </div>
                            )
                        })}
                    </div> 
                </Form>
    )
}

export default PostForm;
