const initialState= {
    mainPosts: [{
        User:{
            id: 1,
            nickname: 'Jay',
        },
        content: '첫 번째 게시글',
        img: 'https://images.unsplash.com/photo-1575550590262-4ad1d8738faa?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=634&q=80'
    }],
    imagePaths: [], // 미리보기 이미지 경로
    addPostErrorReason: false, //포스트 어ㅗㅂ로드 시랲 사유 
    isAddingPost: false, //포스트 업로드 중 
}

export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';

export const LOAD_HASHTAG_POSTS_REQUEST= 'LOAD_HASHTAG_POSTS_REQUEST';
export const LOAD_HASHTAG_POSTS_SUCCESS= 'LOAD_HASHTAG_POSTS_SUCCESS';
export const LOAD_HASHTAG_POSTS_FAILURE= 'LOAD_HASHTAG_POSTS_FAILURE';

const ADD_DUMMY = 'ADD_DUMMY';

export const addPost = {
    type: ADD_POST,
}

export const addDummy = {
    type: ADD_DUMMY,
    data: {
        content: 'Hello',
        userId: 1, 
        User: {
            nickname: 'Jay'
        }
    }
}; 

const reducer = (state=initialState, action) => {
    switch(action.type){
        case ADD_POST: {
            return {
                ...state, 
            };
        }
        case ADD_DUMMY: {
            return {
                ...state,
                mainPosts: [...state.mainPosts,, action.data]
            };
        }
        default: {
            return {
                ...state 
            }
        }
    }
}   

export default reducer;
