import React from 'react';
import Link from 'next/link';
import Head from 'next/head';
import AppLayout from '../components/AppLayout';
import { Input,Form,Button,Card, Icon, Avatar} from 'antd';
// 메인화면 루트 파일 next는 import react를 하지 않아도 된다.  
const dummy = {
    isLoggedIn : true,
    imagePaths: [],
    mainPosts: [{
        User:{
            id: 1,
            nickname: 'Jay',
        },
        content: '첫 번째 게시글',
        img: 'https://images.unsplash.com/photo-1575550590262-4ad1d8738faa?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=634&q=80'
    }],
}

const Home = () => {
    return(
        <div>
            {dummy.isLoggedIn && 
            <Form style={{margin: '10px 0 20px'}}encType="multipart/form-data">
                <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 있었나요?"/>    
                <div>
                    <input type="file" multiple hidden />
                    <Button> 이미지 업로드</Button>
                    <Button type="primary" style={{float:'right'}} htmlType="submit">짹쨱</Button>
                </div>
                <div>
                    {dummy.imagePaths.map( (v,i) => {
                        return (
                            <div key={v} style={{display: 'inline-block'}}>
                                <img src={'http://localhost:3000/' + v} style={{width:'200px'}} alt={v} />
                                <div>
                                    <Button>제거</Button>
                                </div>
                            </div>
                        )
                    })}
                </div> 
            </Form>}
            {dummy.mainPosts.map( (c) => {
                return(
                    <Card 
                        key={+c.createdAt}
                        cover={c.img && <img alt="example" src={c.img} />}
                        actions={[
                            <Icon type="retweet" key="retweet" />,
                            <Icon type="heart" key="heart" />,
                            <Icon type="message" key="message" />,
                            <Icon type="ellipsis" key="ellipsis" />
                        ]}
                        extra={<Button>팔로우</Button>}
                    >
                        <Card.Meta
                            avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                            title={c.User.nicname}
                            description={c.content}
                        />
                    </Card>
                )
            })}
        </div>
    )
}

export default Home;
