import React,{useState} from 'react';
import Head from 'next/head';
import {Form, Input, Checkbox, Button} from 'antd';

import AppLayout from '../components/AppLayout';

const Signup = () => {
    const [userCredentials, setCredentials]= useState({
        id: '',
        nick: '', 
        email: '',
        pass: '',
        pass_chk :'',
        term: false, 
    })
    const onSubmit = (e) => {
        e.preventDefault();
        
    }
    const onChange = (e) => {
        const {name,value} = e.target;
        if(name==='term'){
            setCredentials({[name]: e.target.checked});
        }
        else setCredentials({[name]:value});
        console.log(value);
    }

    return (
        <>
            <Head>
                <title>NodeBird</title>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.23.6/antd.min.css"/>
            </Head>
            <AppLayout>
                <Form onSubmit={onSubmit} style={{padding:10}}>
                    <div>
                        <label htmlFor="id">아이디</label>
                        <br/>
                        <Input name="id" required onChange={onChange}/>
                    </div>
                    <div>
                        <label htmlFor="nick">닉네임</label>
                        <br/>
                        <Input name="nick" required onChange={onChange}/>
                    </div>
                    <div>
                        <label htmlFor="pass">비밀번호</label>
                        <br/>
                        <Input name="pass" type="password" required onChange={onChange}/>
                    </div>
                    <div>
                        <label htmlFor="pass_chk">비밀번호 확인</label>
                        <br/>
                        <Input name="pass_chk" type="password" required onChange={onChange}/>
                    </div>
                    <div>
                        <Checkbox name="term"  value={userCredentials.term} onChange={onChange}>회원가입에 동의합니다.</Checkbox>
                    </div>
                    <div>
                        <Button type="primary" htmlType="submit">가입하기</Button>
                    </div>
                </Form>
            </AppLayout>
        </>
    )
};

export default Signup;
