import {all, fork, takeLatest, call, put ,take} from 'redux-saga/effects';
import {LOG_IN, LOG_IN_SUCCESS, LOG_IN_FAILURE} from '../reducers/user';

const HELLO_SAGA = 'HELLO_SAGA'; 

function loginAPI() {
    //서버에 API 요청을 보내는 부분 
}
function* login(){
    try{
        yield call(loginAPI); //실패하면 catch(e)로 
        yield put({ //loginAPI 성공 
            type: LOG_IN_SUCCESS
        })
    } catch(e) {
        console.error(e); 
        yield put( {//loginAPI 실패 
            type: LOG_IN_FAILURE
        })
    }
}
function* watchLogin() {
    yield takeLatest(LOG_IN, login);
}
function* hello() {
    try{
        yield put( {
            type:'HELLO_TWO'
        });
        console.log('hello'); 
    } catch(e){
        console.error(e); 
    }
}
function* helloSaga(){
    //take가 HELLO_SAGA 타입의 액션이 들어왔을 때 재개하겠다.  
    //take 함수 안에 next가 들어있다. 
    yield t,akeLatest(HELLO_SAGA, hello); 
    console.log('hello Saga');
}
export default function* userSaga() {
    yield all([
        fork(watchLogin),
        fork(helloSaga),
    ]);
}
